/*
 *	addIssue.js
 */
var AddIssue = exports.AddIssue = function() {
};

var querystring = require('querystring'),
    util = require('util');
    dbAccess = require('./dbAccess'),
	url = require('url'),
	tags = require('tags.js');

/* 
 * Handle POST form	
 */
AddIssue.add = function(req, res) {
	if (req.method == 'POST') {
		// from the form data given, parse them according to their respective form objects
		req.addListener('data', function(chunk) {
			var POST = querystring.parse(chunk);
			res.writeHead(200);
			// insert issue from the form
			dbAccess.create('issues', { values: ['user_id="332338"',
                                            'status="inonline"',
                                            'title=\"' + POST.title +'\"',
                                            'description="'+ POST.description +'"',
                                            'link="' + POST.link+'"',
                                            'location="' + POST.location+'"']}, function(error, id) {
				if (error) {
					util.log("Error inserting into database!"); 
					util.log(error); 
					dberror = true;
					res.end('<html><body><h1>Failed to Add Issue </h1></body></html>');
				}
				else {
					util.log("New issue with id " + id + " added to the DB successfully.");
					console.log("user_id = " + POST.title); 
					console.log("title = " + POST.title); 
					console.log("description = " + POST.description); 
					console.log("link = " + POST.link); 
					console.log("location = " + POST.location); 
					console.log("tags = " + POST.tags); 
					// Now add the tags
					if (POST.tags) {
						tags.tagIssue(id, POST.tags, function (error) {
							if (error) {
								util.log("error adding tags in addIssue");
								res.end('<html><body><h1>Failed to Add Issue... the problem was with tagging them</h1></body></html>');
							}
							else {
								util.log("Issue " + id + " tagged successfully.");
								res.end('<html><body><h1>Issue Added Successfully</h1></body></html>');
							}
						});
					}
					else {
						util.log("no tags to add..done!");
						util.log("Issue " + id + " tagged successfully.");
						res.end('<html><body><h1>Issue Added Successfully</h1></body></html>');
					}
				}
			});
		}); // end listener
	} // end if
}

