Mustache = require('mustache'),
fs = require('fs');

exports.render = function(template, view, partials, send_fun){
	res = this;
	fs.readFile(template, function(error, data){
		rawHTML = Mustache.to_html(data.toString(), view, partials, send_fun)
		res.write(rawHTML);
		res.end();
	});
}

exports.redirectTo = function(route){
	this.statusCode = 302;
	this.setHeader('Location', route);
	this.end();
}